import React from 'react'
import './Login.css'
import axios from 'axios'
// import NavBar from './NavBar'
import Nav from './Nav'
import { Link } from 'react-router-dom'
import { useState, useEffect } from 'react'
// import { defaultMethod } from 'react-router-dom/dist/dom'
export default function Login() {
  const [name,setName]=useState(" ")
  const [password,setPassword]=useState(" ")

  async function submit(e){
    e.preventDefault();

    try{
      await axios.post("https://localhost:7000/",{
        name,password

      })
    }

    catch(e){
      console.log(e);
    }
  }
  return (
    <div>
      {/* <NavBar/>*/}
      <Nav />
      <div class="login-box">
        <h2>Login</h2>
        <form action='POST'>
          <div class="user-box">
            <input type="text" onChange={(e) => { setName(e.target.value) }} placeholder="Username" name="username" required="" />
            <label>Username</label>
          </div>
          <div class="user-box">
            <input type="password" onChange={(e) => { setPassword(e.target.value) }} placeholder="Password" name="password" required="" />
            <label>Password</label>
          </div>
          <a href="#" onClick={submit}>
            <span></span>
            <span></span>
            <span></span>
            <span></span>
            Login
          </a>
        </form>
      </div>
      <h4>I didn't have account ? <Link to='/signup' className='sinback'>Signup</Link></h4>
    </div>

  )
}
