import React, { useState } from 'react';
import './Login.css';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Nav from './Nav';

export default function Login() {
   const [name, setName] = useState("");
   const [password, setPassword] = useState("");

   const handleSubmit = (e) => {
      e.preventDefault();
      axios.post('http://localhost:7000/login', { name, password })
         .then(result => console.log(result))
         .catch(err => console.log(err));
   };

   return (
      <div>
         <Nav />
         <div className="login-box">
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
               <div className="user-box">
                  <input type="text" onChange={(e) => setName(e.target.value)} placeholder="Username" name="username" required />
                  <label>Username</label>
               </div>
               <div className="user-box">
                  <input type="password" onChange={(e) => setPassword(e.target.value)} placeholder="Password" name="password" required />
                  <label>Password</label>
               </div>
               <button type="submit">Login</button>
            </form>
         </div>
         <h4>Don't have an account? <Link to='/signup' className='sinback'>Signup</Link></h4>
      </div>
   );
}
