import React from 'react'
import './Login.css'
import axios from 'axios'
// import NavBar from './NavBar'
import Nav from './Nav'
import { Link, useNavigate } from 'react-router-dom'
import { useState, useEffect } from 'react'
// import { defaultMethod } from 'react-router-dom/dist/dom'
export default function Login() {
   const [name,setName]=useState(" ")
   const [password,setPassword]=useState(" ")
   const navigate =useNavigate

  // async function submit(e){
  //   e.preventDefault();

  //   try{
  //     await axios.post("https://localhost:7000/",{
  //       name,password

  //     })
  //   }

  //   catch(e){
  //     console.log(e);
  //   }
  // }
  const handleSubmit =(e)=>{
    e.preventDefault();
    axios.post('http://localhost:7000/login',{
      name,password
    })
    .then(result=>{console.log(result)
      navigate('/template')
    })
    .catch(err=>console.log(err))
  };
  return (
    <div>
      {/* <NavBar/>*/}
      <Nav />
      <div className="login-box">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="user-box">
            <input type="text" onChange={(e)=>setName(e.target.value)} placeholder="Username" name="username" required="" />
            <label>Username</label>
          </div>
          <div className="user-box">
            <input type="password"onChange={(e)=>setPassword(e.target.value)} placeholder="Password" name="password" required="" />
            <label>Password</label>
          </div>
          {/* onChange={(e) => { setName(e.target.value) }} */}
          {/* <a href="#" onSubmit={handleSubmit}>
            <span></span>
            <span></span>
            <span></span>
            <span></span>
            Login
          </a> */}
          <button type="submit">Login</button>
        </form>
      </div>
      <h4>I didn't have account ? <Link to='/signup' className='sinback'>Signup</Link></h4>
    </div>

  )
}
